import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Head from 'next/head'

//styling
import {
  Container, Row, InputGroup, InputGroupAddon, Input, Button,
  Dropdown, DropdownToggle, DropdownMenu, DropdownItem
} from 'reactstrap';

//icons & colors
import { GiEarthAfricaEurope } from 'react-icons/gi'
import { IoIosMenu } from 'react-icons/io'
import { FaSearch } from 'react-icons/fa'
import { COLORS } from '../Helper/Constants'

class Header extends Component {
  render() {
    const { title } = this.props

    return (
      <div>
        <Head>
          <title>Dealbab</title>
          <link href="https://fonts.googleapis.com/css2?family=Comfortaa:wght@700&family=Nunito:wght@300&display=swap" rel="stylesheet" />
          <link href="https://cdnjs.cloudflare.com/ajax/libs/meyer-reset/2.0/reset.min.css" rel="stylesheet" />
          <link href="/static/app.css" rel="stylesheet" />
        </Head>

        <div id="main-header">
          <Container>
            <Row>
              <IoIosMenu color={COLORS.BLACK} className="menu-icon" />
              <div>
                <p id="title">Dealbab</p>
                <p id="sub-title">Explore more</p>
              </div>
              {this.renderSearchBar()}
              {this.renderHeaderDropDown()}
            </Row>
          </Container>
        </div>
      </div>
    )
  }

  renderSearchBar = () => {
    return (
      <InputGroup id="search-bar">
        <Input placeholder="What are you looking for?" className="search-input" />
        <InputGroupAddon addonType="append">
          <Button className="search-button">
            <FaSearch className="search-icon" />
          </Button>
        </InputGroupAddon>
      </InputGroup>
    )
  }

  renderHeaderDropDown = () => {
    return (
      <Dropdown isOpen={dropdownOpen} toggle={toggle}>
        <DropdownToggle caret>
          Hello.Log In
        </DropdownToggle>
        <DropdownMenu>
          <DropdownItem header>Header</DropdownItem>
          <DropdownItem>Some Action</DropdownItem>
          <DropdownItem disabled>Action (disabled)</DropdownItem>
          <DropdownItem divider />
          <DropdownItem>Foo Action</DropdownItem>
          <DropdownItem>Bar Action</DropdownItem>
          <DropdownItem>Quo Action</DropdownItem>
        </DropdownMenu>
      </Dropdown>
    )
  }
}

Header.propTypes = {
  title: PropTypes.string.isRequired,
}

export default Header
