import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Head from 'next/head'

//styling
import { Container, Row, InputGroup, Input, Button } from 'reactstrap';
import { GiEarthAfricaEurope } from 'react-icons/gi'
import { IoIosMenu } from 'react-icons/io'
import { COLORS } from '../Helper/Constants'

class Header extends Component {
  render() {
    const { title } = this.props

    return (
      <div>
        <Head>
          <title>Dealbab</title>
          <link href="https://fonts.googleapis.com/css2?family=Comfortaa:wght@700&family=Nunito:wght@300&display=swap" rel="stylesheet" />
          <link href="https://cdnjs.cloudflare.com/ajax/libs/meyer-reset/2.0/reset.min.css" rel="stylesheet" />
          <link href="/static/app.css" rel="stylesheet" />
        </Head>

        <div id="main-header">
          <Container>
            <Row>
              <IoIosMenu color={COLORS.BLACK} className="menu-icon" />
              <div>
                <p id="title">Dealbab</p>
                <p id="sub-title">Explore more</p>
              </div>
              {this.renderSearchBar()}
            </Row>
          </Container>
        </div>
      </div>
    )
  }

  renderSearchBar = () => {
    return (
      <InputGroup id="search-bar">
        <Input placeholder="What are you looking for?" className="search-input" />
      </InputGroup>
    )
  }
}

Header.propTypes = {
  title: PropTypes.string.isRequired,
}

export default Header
