import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Head from 'next/head'
import { withTranslation } from '../i18n'


//styling
import {
  Container, Row, Button, Card, CardImg, CardBody, CardTitle, CardSubtitle, CardText
} from 'reactstrap';

//icons & colors
import { FaSearch } from 'react-icons/fa'
import { COLORS } from '../Helper/Constants'


class MainPage extends Component {
  constructor(props) {
    super(props)
    this.state = {

    }
  }

  render() {
    const { favIndex } = this.state;
    const { t } = this.props

    return (
      <div className="main-page-container">
        {this.renderBodyContainer()}
      </div>
    )
  }

  /*
  .##........#######...######...####..######...######.
  .##.......##.....##.##....##...##..##....##.##....##
  .##.......##.....##.##.........##..##.......##......
  .##.......##.....##.##...####..##..##........######.
  .##.......##.....##.##....##...##..##.............##
  .##.......##.....##.##....##...##..##....##.##....##
  .########..#######...######...####..######...######.
  */

  _toggleDropDown = () => {
    this.setState({ dropdownOpen: !this.state.dropdownOpen })
  }

  /*
  ..######...#######..##.....##.########...#######..##....##.########.##....##.########..######.
  .##....##.##.....##.###...###.##.....##.##.....##.###...##.##.......###...##....##....##....##
  .##.......##.....##.####.####.##.....##.##.....##.####..##.##.......####..##....##....##......
  .##.......##.....##.##.###.##.########..##.....##.##.##.##.######...##.##.##....##.....######.
  .##.......##.....##.##.....##.##........##.....##.##..####.##.......##..####....##..........##
  .##....##.##.....##.##.....##.##........##.....##.##...###.##.......##...###....##....##....##
  ..######...#######..##.....##.##.........#######..##....##.########.##....##....##.....######.
  */

  renderBodyContainer = () => {
    const { t } = this.props

    return (
      <Container fluid className="spacing">
        <div className="products-section">
          <h3 className="section-title">{t('featured-products')}</h3>
          <Row>
            <Card>
              <CardImg top width="100%" src="/assets/318x180.svg" alt="Card image cap" />
              <CardBody>
                <CardTitle>Card title</CardTitle>
                <CardSubtitle>Card subtitle</CardSubtitle>
                <CardText>Some quick example text to build on the card title and make up the bulk of the card's content.</CardText>
                <Button>Button</Button>
              </CardBody>
            </Card>
          </Row>
        </div>
      </Container>
    )
  }
}

MainPage.propTypes = {
  t: PropTypes.func.isRequired,
}

export default withTranslation('mainpage')(MainPage)
